export const initialState = {
    user: null,
    playlists: [],
    playing: false,
    item: null,
    token: null
    //token: "BQAXInuvbmNyYAlkqh1kUBwmNXJB5OVn3qXJcHgCE33y9FET351-wmamDRJGr8lh7i41MPHrFAU1aUidYy6c-dodNZ4-jw7u4nPiSWQoGkPRxAuRhApTBNBlScUZy85aKNlp0x5cZgkjkbj53yQHKyy_Igg"
    //debug purpose only
}

const reducer = (state, action) => {
    console.log(action)

    //Action -> type, [payload]
    switch(action.type)
    {
        case 'SET_USER':
            return {
                ...state, //keep whatever is on the current state
                user : action.user
            }
        case 'SET_TOKEN':
            return {
                ...state,
                token : action.token
            }
        case 'SET_PLAYLISTS':
            return {
                ...state,
                playlists : action.playlists
            }
        case 'SET_DISCOVER_WEEKLY':
                return {
                    ...state,
                    discover_weekly : action.discover_weekly
                }

        default:
            return state

    }
}

export default reducer
