export const initialState = {
    user: null,
    playlists: [],
    playing: false,
    item: null
}

const reducer = (state, action) => {
    console.log(action)

    //Action -> type, [payload]
    switch(action.type)
    {
        case 'SET_USER':
            return {
                ...state, //keep whatever is on the current state
                user : action.user
            }
        default:
            return state

    }
}

export default reducer
